/**
 * 
 * 
 * You are given two strings, s and t which only consist of lowercase letters. t is generated by shuffling the letters in s as well as potentially adding an additional random character. Return the letter that was randomly added to t if it exists, otherwise, return ’  ‘.
 * Note: You may assume that at most one additional character can be added to t. 
 * 
  s = "foobar", t = "barfoot", return 't'
  s = "ide", t = "idea", return 'a'
  s = "coding", t "ingcod", return ''
 * 
 */

// T - O(s+t)
// S - O(s)
function spotTheDifference(s, t) {
  const characterFrequency = new Map();

  for(let i=0; i<s.length; i++) {
    const character = s[i];

    const count = characterFrequency.get(character);

    if(count === undefined) {
      characterFrequency.set(character, 1);
    } else {
      characterFrequency.set(character, count + 1);
    }
  }

  for(let i=0; i<t.length; i++) {
    const character = t[i];

    const count = characterFrequency.get(character);

    if(count === undefined || count === 0) {
      return character;
    } else {
      characterFrequency.set(character, count - 1);
    }
  }

  return '';
}

console.log(spotTheDifference("foobar", "barfoot") === 't')
console.log(spotTheDifference("ide", "idea") === 'a')
console.log(spotTheDifference("coding", "ingcod") === '')